import React, { useEffect, useState } from "react";
import ReactHtmlParser from 'html-react-parser';

function About() {

    const [settings, setSettings] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);

    useEffect(() => {
        fetch("http://127.0.0.1:8000/api/settings")
        .then(res => res.json())
        .then(
            result => {
                setIsLoaded(true);
                setSettings(result);
            },
            error => {
                setIsLoaded(true);
            }
        );
    }, []);
  return (
    <section
      id="aboutMe"
      className="container d-flex flex-column justify-content-center align-items-center align-items-md-start w-100 sectionDivider gap-4"
    >
      <div className="row w-100">
        <div className="col-12">
          <h2 className="text-white display-1 font-weight-bold text-center">
            <strong
              className="sectionTitle"
              data-aos="fade-in"
              data-aos-delay="0"
              data-aos-duration="1000"
            >
              About Me
            </strong>
          </h2>
        </div>
      </div>
      <div className="row w-100">
        <div
          className="col-12 text-white lead text-center"
          style={{ fontSize: "1.6rem" }}
        >
          {isLoaded ? ReactHtmlParser(settings) : ''}
        </div>
      </div>
    </section>
  );
}

export default About;
